#include <fstream> #include <iostream> #include <string> #include <iomanip> #include "ItemType.h"using namespace std;void ItemType::WriteItemToFile (ofstream& outFile) {    outFile << idis() << " " <<  gradeis() << endl;}void ItemType::setStudent(int inStudentID, char inGrade, char inMajor, char inGender){    studId = inStudentID;    grade = inGrade;    major = inMajor;    gender = inGender;}void ItemType::setID(int id){	studId = id;}int ItemType::idis() const{    return studId;}    char ItemType::gradeis() const{    return grade;}  char ItemType::majoris() const{    return major;}  char ItemType::genderis() const{    return gender;}RelationType ItemType::ComparedTo(ItemType& item) const    // Purpose: To compare to items of the same type.    // Input: info.    // Pre: both Objects are constructed, and all values are assigned a value.    // Output: bool.    // Post: Items are compared and a bool is returned.    // Note: none    {      if(studId < item.idis())        return LESS;      else if (studId > item.idis())        return GREATER;      else         return EQUAL;        }